def calculate_name(name):

    number = 0
    for i in name:
        if i==' ':
            continue
        i = i.upper()
        if dictionary.get(i) is None:
            return -1
        number += dictionary[i]
    name = str(number)
    while len(name) > 1:
        number = 0
        for i in name:
            number+=int(i)
        name = str(number)
    return name_result[int(name)-1]
